import React from 'react';
import './NavBar.css';
import {Button, ExpandingButton}  from './CustomNavButton.js'
import { FaGithub, FaLinkedin } from 'react-icons/fa';
export default class NavBar extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            atTop: true,
            expandedNav: false
        };
    }
    componentWillReceiveProps(nextProps){

        this.updateState();
    }
    handleExpandClick(){
        console.log("expand navbar");
        this.setState({expandedNav: true});
    }
    updateState(){
        if(this.state.atTop != this.props.atTop)
            this.setState({atTop: this.props.atTop});
        if(this.state.atTop){
            if(this.state.expandedNav != false){
                this.setState({expandedNav: false});
            }
        }
    }
    render() {

        return(
            <div>
                {this.state.atTop ? (
                    <div className='NavBar'>
                        <Button>Link 1</Button>
                        <Button>Link 2</Button>
                        <ExpandingButton
                            redirect="https://github.com/PatrickBrown1">
                            <FaGithub size={32} style={{verticalAlign: 'middle'}}/>
                            &thinsp; GitHub

                        </ExpandingButton>
                        <ExpandingButton
                            redirect="https://www.linkedin.com/in/P-Brown">
                            <FaLinkedin size={32} style={{verticalAlign: 'middle'}}/>
                            &thinsp; LinkedIn
                        </ExpandingButton>
                    </div>
                ) : [
                    this.state.expandedNav ? (
                        <div className='NavBar'>
                        <Button>Link 9</Button>
                        <Button>Link 2</Button>
                        <ExpandingButton
                            redirect="https://github.com/PatrickBrown1">
                            <FaGithub size={32} style={{verticalAlign: 'middle'}}/>
                            &thinsp; GitHub

                        </ExpandingButton>
                        <ExpandingButton
                            redirect="https://www.linkedin.com/in/P-Brown">
                            <FaLinkedin size={32} style={{verticalAlign: 'middle'}}/>
                            &thinsp; LinkedIn
                        </ExpandingButton>
                    </div>
                    ) : (
                        <div className='NavBar'>
                            <Button onClick={ () => this.handleExpandClick() }>
                                Expand
                            </Button>
                        </div>
                    )
                ]}
            </div>
        );
    }
}
