import React from 'react';
import './App.css';
import NavBar from'./NavBar.js';
import Background from'./Background.js';

function getScrollPosition({ element, useWindow }) {

  const target = element ? element.current : document.body
  const position = target.getBoundingClientRect()

  return useWindow
    ? { x: window.scrollX, y: window.scrollY }
    : { x: position.left, y: position.top }
}

function App() {
  if(getScrollPosition(App, this).y!=0){
    alert("not 0!");
  }
  return (
    <div className="App">
      <NavBar/>
      <Background/>

      <div>
        <header className="App-header">
          <p className="Title">Patrick Brown's Personal Website!</p>
        </header>
        <body className="App-body">
          <p>Make header first because why not</p>
          <p2>Make the header normal for over 50% window size</p2>
          <p2>Then make it a square button three lines if it's under 50%</p2>
          <p2>As soon as the screen is not at the top, change navbar to a circle three lines button on right</p2>
        </body>
      </div>
    </div>
  );
}

export default App;
